    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        LeyCosenos c = new LeyCosenos();
        c.setA(Double.parseDouble(jTextField1.getText()));
        c.setC(Double.parseDouble(jTextField3.getText()));
        c.setB(Double.parseDouble(jTextField2.getText()));
        jTextField5.setText(Double.toString(c.CalcularBeta()));
        jTextField4.setText(Double.toString(c.CalcularAlpha()));
        jTextField6.setText(Double.toString(c.CalcularGamma()));
    }//GEN-LAST:event_jButton1ActionPerformed

